definition.script:
pipeline {
    agent any
    stages {
        stage("Cleanup") {
            steps {
                deleteDir()
            }
        }
        stage("Clone repo") {
            steps {
                script {
                    repo = params.repo
                    sh 'git clone $repo'
                }
            }
        }

        stage("Build docker image") {
            steps {
                dir("draft/app") {
                    script {
                        docker.build("consul-app:latest")
                    }
                }
            }
        }
    }
}
